/// <summary>
/// The <c>MCRBrokerClaimProcessingArgs</c> class is used to pass parameters between the user dialog
/// before processing a claim and the claim processing routine.
/// </summary>
class MCRBrokerClaimProcessingArgs
{
    MCRBrokerClaimWriteOffCode writeOffReasonCode;
    InvoiceDate                invoiceDate;

}

Method.Source:
public MCRBrokerClaimWriteOffCode parmWriteOffReasonCode(MCRBrokerClaimWriteOffCode _writeOffReasonCode = writeOffReasonCode)
{
    writeOffReasonCode = _writeOffReasonCode;

    return writeOffReasonCode;
}

public InvoiceDate parmInvoiceDate(InvoiceDate _invoiceDate = invoiceDate)
{
    invoiceDate = _invoiceDate;

    return invoiceDate;
}
