/// <summary>
/// Gets/Sets the Broker Amount.
/// </summary>
/// <param name = "_brokerAmount">The new broker amount, or empty to retrieve the current Broker amount.</param>
/// <returns>Returns the current/new broker amount.</returns>
public AmountCur parmBrokerAmount(AmountCur _brokerAmount = brokerAmount)
{
    brokerAmount = _brokerAmount;
    return brokerAmount;
}

