/// <summary>
/// The <c>MCRMergeConfirmationConfigure</c> class gets and sets the merged from and to customers
/// used in customer merge functionality.
/// </summary>
class MCRMergeConfirmationConfigure
{
    CustAccount custAccountMergedFrom;
    CustAccount custAccountMergedTo;
}

Method.Source:
    public CustAccount parmMergedFromCust(CustAccount _mergedFromCust = custAccountMergedFrom)
{
    custAccountMergedFrom = _mergedFromCust;
    return custAccountMergedFrom;
}

    public CustAccount parmMergedToCust(CustAccount _custAccountMergedTo = custAccountMergedTo)
{
    custAccountMergedTo = _custAccountMergedTo;
    return custAccountMergedTo;
}
