Method.Source:
protected void loadModule()
{
    NumberSeqDatatype datatype = NumberSeqDatatype::construct();

    datatype.parmDatatypeId(extendedTypeNum(EInvoiceTransmissionName_IT));
    datatype.parmReferenceHelp(literalStr("@SYP4881711"));
    datatype.parmWizardIsContinuous(false);
    datatype.parmWizardIsManual(NoYes::No);
    datatype.parmWizardFetchAheadQty(10);
    datatype.parmWizardIsChangeDownAllowed(NoYes::No);
    datatype.parmWizardIsChangeUpAllowed(NoYes::No);
    datatype.parmSortField(1);

    datatype.addParameterType(NumberSeqParameterType::DataArea, true, false);
    this.create(datatype);

    datatype.parmDatatypeId(extendedTypeNum(EInvoiceFileName_IT));
    datatype.parmReferenceHelp(literalStr("@SYP4881755"));
    datatype.parmWizardIsContinuous(false);
    datatype.parmWizardIsManual(NoYes::No);
    datatype.parmWizardFetchAheadQty(10);
    datatype.parmWizardIsChangeDownAllowed(NoYes::No);
    datatype.parmWizardIsChangeUpAllowed(NoYes::No);
    datatype.parmSortField(2);

    datatype.addParameterType(NumberSeqParameterType::DataArea, true, false);
    this.create(datatype);

    datatype.parmDatatypeId(extendedTypeNum(CFDIWithholdingJournalID_MX));
    datatype.parmReferenceLabel(literalStr("@SYS17795"));
    datatype.parmReferenceHelp(literalStr("@MexicoCFDIWithholding:WithholdingJournalID_Help"));
    datatype.parmWizardIsContinuous(false);
    datatype.parmWizardIsManual(NoYes::No);
    datatype.parmWizardIsChangeDownAllowed(NoYes::No);
    datatype.parmWizardIsChangeUpAllowed(NoYes::No);
    datatype.parmSortField(3);
    datatype.addParameterType(NumberSeqParameterType::DataArea, true, false);
    this.create(datatype);
}

/// <summary>
/// Appends the current class to the map that links modules to number sequence data type generators.
/// </summary>
[SubscribesTo(classstr(NumberSeqGlobal),delegatestr(NumberSeqGlobal,buildModulesMapDelegate))]
static void buildModulesMapSubsciber(Map numberSeqModuleNamesMap)
{
    NumberSeqGlobal::addModuleToMap(classnum(NumberSeqModuleEInvoice), numberSeqModuleNamesMap);
}
