/// <summary>
/// this class is used to configure number sequences in Russian HRM module.
/// </summary>
/// <remarks>
/// the details of configuring number sequence is in method loadModule().
/// </remarks>
public class NumberSeqModuleRHRM extends NumberSeqApplicationModule
{
}

Method.Source:
/// <summary>
///    Configures all of the data types that are used by the module.
/// </summary>
protected void loadModule()
{
    Counter             counter     = 1;
    NumberSeqDatatype   datatype    = NumberSeqDatatype::construct();

    datatype.parmDatatypeId(extendedTypeNum(RHRMJournalNum));
    datatype.parmReferenceHelp(literalstr("@RUP1965"));
    datatype.parmWizardIsContinuous(false);
    datatype.parmWizardfetchAheadQty(10);
    datatype.parmWizardIsManual(NoYes::No);
    datatype.parmWizardIsChangeDownAllowed(NoYes::No);
    datatype.parmWizardIsChangeUpAllowed(NoYes::No);
    datatype.parmSortField(counter);
    counter = counter + 1;

    datatype.addParameterType(NumberSeqParameterType::DataArea, true, false);
    this.create(datatype);

    datatype.parmDatatypeId(extendedTypeNum(RHRMOrderNum_trip));
    datatype.parmReferenceHelp(literalstr("@RUP1636"));
