/// <summary>
/// Base class visitor, used for visiting the adaptor data structure.
/// </summary>
abstract class PcAdaptorVisitor
{
    boolean visitAttributes;

}

Method.Source:
public boolean parmVisitAttributes(boolean _visitAttributes = visitAttributes)
{
    visitAttributes = _visitAttributes;

    return visitAttributes;
}

