using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

[ExportAttribute(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
internal final class ProductionAndShopFloorJobIdNumberSeqFeature implements IFeatureMetadata, IFeatureLifecycle, IFeatureMetadataEnablementNotifiable
{
    private static ProductionAndShopFloorJobIdNumberSeqFeature instance;

}

Method.Source:
private static void TypeNew()
{
    instance = new ProductionAndShopFloorJobIdNumberSeqFeature();
}

[Hookable(false)]
public void onEnabled()
{
    ttsbegin;

    DataArea dataArea;

    while select id from dataArea where dataArea.isVirtual == NoYes::No
    {
        changecompany(dataArea.id)
        {
            ProductionAndShopFloorJobIdNumberSeqHelper::ensureProductionAndShopFloorJobIdNumberSequence();
        }
    }

    ttscommit;
}

[Hookable(false)]
public void onDisabled()
{
    // nothing to do
}
