using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.Implementation;

/// <summary>
/// This class defines the project budget management feature details of Project Operations for non-stocked/resource based scenario
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
[FeatureMetadataConfigurationKey(configurationKeyStr(Project))]
internal final class ProjBudgetManagementFeature implements IFeatureMetadata, IFeatureLifecycle, IFeatureDependencies
{

    private static ProjBudgetManagementFeature instance;

}

Method.Source:
private static void TypeNew()
{
    instance = new ProjBudgetManagementFeature();
}

/// <summary>
/// Obtains the singleton object instance.
/// </summary>
/// <returns>The <c>ProjBudgetManagementFeature</c> instance.</returns>
public static ProjBudgetManagementFeature instance()
{
    if (!instance)
    {
        instance = new ProjBudgetManagementFeature();
    }
    return instance;
}

/// <summary>
/// Gets the label Id of the feature that is shown on the feature management workspace.
/// </summary>
/// <returns>Label for the feature.</returns>
[Hookable(false)]
public FeatureLabelId label()
{
    return literalStr("@Proj:ProjBudgetManagementFeatureLabel");
}

/// <summary>
/// Gets the application module of the feature that is displayed on the feature management workspace.
/// </summary>
/// <returns>
/// The application module value of the feature that is displayed on the feature management workspace.
/// </returns>
[Hookable(false)]
public int module()
{
    return FeatureModuleV0::ProjectManagementAndAccounting;
}

/// <summary>
/// Gets the label Id that is displayed as summary of the feature.
/// </summary>
/// <returns>Summary for the feature.</returns>
[Hookable(false)]
public FeatureLabelId summary()
{
    return literalStr("@Proj:ProjBudgetManagementFeatureSummary");
}

