{
    ProjJournalTrans    projJournalTrans;
    CurrencyCode        salesCurrencyCode;
    ProjCategoryId      projCategoryId;
    ProjId              projId;
    TransDate           transDate;
    ProjTransIdBase     projTransId;
    ProjActivityNumber  activityNumber;
    ProjLinePropertyId  linePropertyId;
}

