/// <summary>
/// Stores commonly used constants across project.
/// </summary>
public class ProjConstants
{
    // lookup control delimiter for multiple inputs
    public const str multipleSelectLEDelimiter = ' ';
    public const str twoParmCacheKeyConst = '%1-%2';

    public const str ItemReqPackingSlipIdFormat = '%1.%2';

    // Deadlock retry interval
    public const int DeadLockRetryInterval = 500;

    // Number of batch subtask
    public const int NumOfBatchSubTasks = 4;

    // General logger message
    public const str GeneralLoggerErrorMsg = 'Method: %1 Error: %2';

	// Prefix for temp Ids used in Proforma Invoice printing
    public const str ProformaInvoicePrefix = 'P_';

	// Prefix for temp Ids used in Adjustment process
    public const str AdjustmentTempIdPrefix = '*_T_';

    // Records selected threshold for presenting adjustment warning message. 
    public const int64 AdjustmentMessageThresholdCount = 1000;

    // Setup value in ProjParameters for Maximum number of lines for Advanced Journals 
    // (note: 10000 is the threshold where we start to see performance degredation during posting)
    public const int AdvancedJournalMaxLinesPerJournal = 10000;

    // Default value in ProjParameters for Maximum number of lines for Advanced Journals
    public const int AdvancedJournalMaxLinesPerJournalDefault = 200;
}
