Method.Source:
protected void action()
    {
        int                     i;

        // We Should NOT call super.
        if (queryRun.changed(tablenum(ProjWIPTable)))
        {
            i++;
            projWIPTable  = queryRun.get(tablenum(ProjWIPTable));
            this.progressSet(i);
            this.parmProjWIPProjId(projWIPTable.ProjId);
            if (this.doAction())
            {
                this.recalculateEstimate();
            }
        }
    }

