/// <summary>
/// The <c>ProjListNode</c> class is a linked list node wrapper for <c>ProjList</c> elements.
/// </summary>
class ProjListNode
{
    str data;
    ProjListNode nextNode;
}

Method.Source:
/// <summary>
/// Initializes a new instance of the <c>ProjListNode</c> class.
/// </summary>
/// <param name="_data">
/// The data of the node.
/// </param>
/// <param name="_nextNode">
/// The node that this one will point to.
/// </param>
public void new(str _data = "", ProjListNode _nextNode = null)
{
    data = _data;
    nextNode = _nextNode;
}

/// <summary>
/// Sets the next node of the list.
/// </summary>
/// <param name="_projListNode">
/// The node to be set.
/// </param>
public void setNextNode(ProjListNode _projListNode)
{
    nextNode = _projListNode;
}
