/// The <c>ProjOnAccountCategoryFeature</c> class allows the ability for a project category to be used for on-account transactions.
/// </summary>
[Export(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnly]
[FeatureMetadataConfigurationKey(configurationKeyStr(Project))]
public final class ProjOnAccountCategoryFeature implements IFeatureMetadata, IFeatureLifecycle
{
    private static ProjOnAccountCategoryFeature instance;

}

