/// <summary>
///    Creates purchase orders based on sales orders of the type requirement generated by the project
///    module.
/// </summary>
class PurchAutoCreate_SalesProjectItemReq
extends PurchAutoCreate_Sales
{
}

Method.Source:
/// <summary>
/// Creates the purchase line and creates a price discount change policy.
/// </summary>
public void createLine()
{
    super();

    purchLine.setPriceDiscChangePolicy(PriceDiscSystemSource::Project);
    purchLine.DefaultDimension = LedgerDimensionDefaultFacade::serviceMergeDefaultDimensions(salesLine.DefaultDimension, purchLine.DefaultDimension);
    if (purchLine.SystemEntryChangePolicy
    && purchLine.SystemEntrySource == PriceDiscSystemSource::Project)
    {
        purchLine.update();
    }
}

/// <summary>
///    Makes the final updates to the newly created purchase order and creates a price discount change
///    policy.
/// </summary>
void endUpdate()
{
    super();

    purchTable.setPriceDiscChangePolicy(PriceDiscSystemSource::Project);
    if (purchTable.SystemEntryChangePolicy
    &&  purchTable.SystemEntrySource == PriceDiscSystemSource::Project)
    {
        purchTable.update();
    }
}

public static PurchAutoCreate_SalesProjectItemReq construct(
    TmpPurchLinePrice           _tmpPurchLinePrice,
    PurchCreateFromSalesOrder  _purchCreateFromSalesOrder = null)
{
    return new PurchAutoCreate_SalesProjectItemReq(_tmpPurchLinePrice, _purchCreateFromSalesOrder);
}

