protected void calcAutomaticTotalDiscount(PurchTable _purchTable)
{
    if (PurchParameters::find().AutomaticTotalDiscount)
    {
        _purchTable = PurchTable::findRecId(_purchTable.RecId, true);
        _purchTable.updateFinalDisc();
    }
}

