SourceCode.Declaration:
using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// When <c>PurchTableWorkflowSubmitPerfEnhancementFeature</c> is enabled, purchase totals and accounting distributions
/// are calculated and validated after when PO is submitted to workflow but only when budget control is not enabled.
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public final class PurchTableWorkflowSubmitPerfEnhancementFeature implements IFeatureMetadata, IFeatureLifecycle
{
    private static PurchTableWorkflowSubmitPerfEnhancementFeature singleton;

}

Method.Source:
[Hookable(false)]
public static PurchTableWorkflowSubmitPerfEnhancementFeature instance()
{
    if (!singleton)
    {
        singleton = new PurchTableWorkflowSubmitPerfEnhancementFeature();
    }

    return singleton;
}
