Method.Source:
/// <summary>
/// Caclulate tax amount by transaction which will not be created.
/// </summary>
/// <returns>
/// The tax amount value.
/// </returns>
public TaxAmount taxWithTaxTrans_RU()
{
    TmpTaxWorkTrans tmpTaxWorkTransLocal;
    TaxAmount       taxAmount;

    tmpTaxWorkTransLocal.setTmpData(this.tax().tmpTaxWorkTrans());

    while select sum(SourceTaxAmountCur), sum(SourceRegulateAmountCur) from tmpTaxWorkTransLocal
        group by TaxAutogenerated
        where tmpTaxWorkTransLocal.WithoutTaxTrans_RU == NoYes::No
    {
        if (tmpTaxWorkTransLocal.TaxAutogenerated)
        {
            taxAmount += tmpTaxWorkTransLocal.SourceTaxAmountCur;
        }
        else
        {
            taxAmount += tmpTaxWorkTransLocal.SourceRegulateAmountCur;
        }
    }

    return taxAmount;
}

/// <summary>
/// Initializes an instance of the <c>PurchTotals</c> class.
/// </summary>
/// <param name="_source">
/// The source record.
/// </param>
/// <param name="_specQty">
/// A value that indicates which quantity will be used.
/// </param>
/// <returns>
/// An instance of the <c>PurchTotals</c> class.
/// </returns>
/// <remarks>
/// This procedure helps guarantee that your customizations are maintained if the base version of the
/// <c>PurchTotals</c> class is changed and minimizes code conflicts during an upgrade.
/// </remarks>
static public PurchTotals construct(PurchTable _source, PurchUpdate _specQty)
{
    return new PurchTotals_TransLedgerCov(_source, _specQty);
}
