using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// Represents functionality which will use file name from ER configuration for expored RPS file.
/// </summary>
[ExportAttribute(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public class RPSExportUseFileNameFromERFeature implements IFeatureMetadata, IFeatureLifecycle
{
    private static RPSExportUseFileNameFromERFeature instance;

}

Method.Source:
private static void TypeNew()
{
    instance = new RPSExportUseFileNameFromERFeature();
}

/// <summary>
/// Obtains the singleton object instance.
/// </summary>
/// <returns>An <c>RPSExportUseFileNameFromERFeature</c> instance.</returns>
[Hookable(false)]
public static RPSExportUseFileNameFromERFeature instance()
{
    return RPSExportUseFileNameFromERFeature::instance;
}

/// <summary>
/// Determines whether feature is enabled.
/// </summary>
/// <returns>True, if feature is enabled; otherwise, false.</returns>
public static boolean isEnabled()
{
    #isoCountryRegionCodes

    return SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBR])
        && Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.FeatureStateProvider::isFeatureEnabled(RPSExportUseFileNameFromERFeature::instance());
}
