using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.Implementation;
using IFeatureMetadata = Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata;
using IFeatureLifecycle = Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureLifecycle;
using FeatureLifecycleStage = Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.FeatureLifecycleStage;

/// <summary>
/// The <c>ReqTransOverviewRequestedDateSortingFeature</c> class adds the option to sort based on the Requested date 
/// field in the Net requirements form.
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[FeatureMetadataConfigurationKey(configurationKeyStr(Req))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
internal final class ReqTransOverviewRequestedDateSortingFeature implements IFeatureMetadata, IFeatureLifecycle
{
    private static ReqTransOverviewRequestedDateSortingFeature singleton;

}

Method.Source:
[Hookable(false)]
public static ReqTransOverviewRequestedDateSortingFeature instance()
{
    if (!singleton)
    {
        singleton = new ReqTransOverviewRequestedDateSortingFeature();
    }

    return singleton;
}

[Hookable(false)]
internal boolean isFeatureAndConfigurationkeyEnabled()
{
    return this.isEnabled() && this.isConfigurationKeyEnabled();
}
