// update soft hour reservation capacity update_recordset _tmpRollUp setting SoftLocked = _tmpRollUp.SoftLocked + assignmentRollup.Capacity join assignmentRollup where assignmentRollup.ActivityCompany == currentCompany && (!_resource || assignmentRollup.Resource == _resource) && assignmentRollup.Resource != 0 && assignmentRollup.Hour >= _rangeStartDateTime && assignmentRollup.Hour <= _rangeEndDateTime && assignmentRollup.CommitType == ResCommitType::SoftBook && _tmpRollUp.Coverage == ResRollupCoverage::Assignment && _tmpRollUp.ActivityResource == assignmentRollup.ActivityResource && _tmpRollUp.Timescale == AVTimeframeType::Hour && _tmpRollUp.Timeframe == assignmentRollup.Hour; break;
case AVTimeframeType::Day: // update hard hour reservation capacity ResAssignmentRollupRootTaskDayView rootTaskDayView; update_recordset _tmpRollUp setting HardLocked = _tmpRollUp.HardLocked + rootTaskDayView.Capacity join rootTaskDayView where rootTaskDayView.ActivityCompany == currentCompany && (!_resource || rootTaskDayView.Resource == _resource) && rootTaskDayView.Resource != 0 && rootTaskDayView.Day >= _rangeStartDateTime && rootTaskDayView.Day <= _rangeEndDateTime && rootTaskDayView.CommitType == ResCommitType::HardBook && _tmpRollUp.Coverage == ResRollupCoverage::Assignment && _tmpRollUp.ActivityResource == rootTaskDayView.ActivityResource && _tmpRollUp.Timescale == AVTimeframeType::Day && _tmpRollUp.Timeframe == rootTaskDayView.Day;
