case AVTimeframeType::Month: // update hard hour reservation capacity ResAssignmentRollupRootTaskMonthView rootTaskMonthView; update_recordset _tmpRollUp setting HardLocked = _tmpRollUp.HardLocked + rootTaskMonthView.Capacity join rootTaskMonthView where rootTaskMonthView.ActivityCompany == currentCompany && (!_resource || rootTaskMonthView.Resource == _resource) && rootTaskMonthView.Resource != 0 && rootTaskMonthView.Month >= _rangeStartDateTime && rootTaskMonthView.Month <= _rangeEndDateTime && rootTaskMonthView.CommitType == ResCommitType::HardBook && _tmpRollUp.Coverage == ResRollupCoverage::Assignment && _tmpRollUp.ActivityResource == rootTaskMonthView.ActivityResource && _tmpRollUp.Timescale == AVTimeframeType::Month && _tmpRollUp.Timeframe == rootTaskMonthView.Month;
