{
    private str address;
    private str subject;
    private str body;

}

Method.Source:
/// <summary>
/// The email address to send the account statement to.
/// </summary>
/// <param name = "_address">The new email address.</param>
/// <returns>The email address to send the account statement to.</returns>
public str parmAddress(str _address = address)
{
    address = _address;
    return address;
}

/// <summary>
/// The subject for the email.
/// </summary>
/// <param name = "_subject">The new subject.</param>
/// <returns>The subject for the email.</returns>
public str parmSubject(str _subject = subject)
{
    subject = _subject;
    return subject;
}

/// <summary>
/// The text for the body of the email.
/// </summary>
/// <param name = "_body">The new value for the body.</param>
/// <returns>The text for the body of the email.</returns>
public str parmBody(str _body = body)
{
    body = _body;
    return body;
}
