/// <summary>
/// The contract class representing the channel database data.
/// </summary>
[DataContractAttribute('ChannelDatabaseConnection')]
public class RetailCDXServiceChannelDatabaseConnectionContract
{
    private str databaseServerName;
    private str databaseName;
    private str userName;
    private str password;
    private str encryptedConnectionString;
    private boolean trustServerCertificate;

}

