Method.Source:
/// <summary>
/// Calculates the detailed status for a sales order.
/// </summary>
/// <param name = "_salesTable">The sales table.</param>
/// <returns>Detailed order status.</returns>
internal static MCRSalesOrderDetailedStatus getDetailedOrderStatus(SalesTable _salesTable)
{
    return MCRSalesDetailedStatus::salesOrderDetailedStatus(_salesTable);
}

/// <summary>
/// Calculates the detailed status for a sales line.
/// </summary>
/// <param name = "_salesLine">The sales line.</param>
/// <returns>Detailed line status.</returns>
internal static MCRSalesLineDetailedStatus getDetailedLineStatus(SalesLine _salesLine)
{
    return MCRSalesDetailedStatus::salesLineDetailedStatus(_salesLine);
}
