using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.Implementation;
using Microsoft.Dynamics.BusinessPlatform.SharedTypes;

/// <summary>
/// Represents a feature enabling asynchronous customer edits.
/// </summary>
[Export(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
internal final class RetailEnableAsyncCustomerEditFeature implements IFeatureMetadata, IFeatureDependencies, IFeatureLifecycle, IFeatureMetadataEnablementNotifiable
{
    private static RetailEnableAsyncCustomerEditFeature instance;

}

Method.Source:
private static void TypeNew()
{
    instance = new RetailEnableAsyncCustomerEditFeature();
}

/// <summary>
/// Obtains the singleton object instance.
/// </summary>
/// <returns>The <c>RetailEnableAsyncCustomerEditFeature</c> instance.</returns>
[Hookable(false)]
public static RetailEnableAsyncCustomerEditFeature instance()
{
    return RetailEnableAsyncCustomerEditFeature::instance;
}

[Hookable(false)]
public void onEnabled()
{
    ttsbegin;

    RetailFunctionalityProfile storeProfile;
    RetailOnlineChannelFunctionalityProfileTable onlineStoreProfile;

    // Update all store functionality profiles to allow creating async customers.
    update_recordset storeProfile setting CreateAsyncCustomers = NoYes::Yes;
    update_recordset onlineStoreProfile setting CreateAsyncCustomers = NoYes::Yes;

    ttscommit;
}

/// <summary>
/// Creates dependency on all the features in the list. Pre-requisites features cannot be disabled unless current feature is disabled.
/// </summary>
/// <returns>An array containing all the pre-requisite features.</returns>
[Hookable(false)]
public IFeature[] dependsOnAllFeatures()
{
    var featureDependency = new IFeature[3]();
    featureDependency.SetValue(RetailCustomerSimpleOptimizedOrderManagementFeature::instance(), 0);
    featureDependency.SetValue(RetailEnhancedAsyncCustCreationFeature::instance(), 1);
    featureDependency.SetValue(RetailAsyncAddressCreationFeature::instance(), 2);
    return featureDependency;
}

/// <summary>
/// Creates an array of the features, if any one is enabled, feature can be enabled.
/// </summary>
/// <returns>An array containing any pre-requisite features.</returns>
[Hookable(false)]
public IFeature[] dependsOnAnyFeature()
{
    return null;
}

/// <summary>
/// Determines if the feature is enabled.
/// </summary>
/// <returns>True if the feature is enabled; otherwise, false.</returns>
internal static boolean isEnabled()
{
    return Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.FeatureStateProvider::isFeatureEnabled(RetailEnableAsyncCustomerEditFeature::instance());
}
