using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.Implementation;
using Microsoft.Dynamics.BusinessPlatform.SharedTypes;

/// <summary>
/// The <c>RetailEnableSalesOrderTotalsFeature</c> class provides a feature control which enables the use of
/// SalesOrderTotals table (pre-calculated totals) which improves the performance of searching for commerce orders.
/// </summary>
[FeatureMetadataConfigurationKey(configurationKeyStr(Retail))]
[Export(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
internal final class RetailEnableSalesOrderTotalsFeature implements IFeatureMetadata, IFeatureLifecycle
{
    private static RetailEnableSalesOrderTotalsFeature instance = new RetailEnableSalesOrderTotalsFeature();

}

