[Hookable(false)]
public void onDisabled()
{
    RetailEventNotificationSettingLine retailEventNotificationSettingLine;
    select firstonly crosscompany RecId from retailEventNotificationSettingLine;

    if (retailEventNotificationSettingLine.RecId)
    {
        throw error("@Retail:RetailEventNotificationPerDeliveryModeFeatureDisableErrorMessage");
    }
}
