using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;
using Microsoft.Dynamics.BusinessPlatform.SharedTypes;

/// <summary>
/// The <c>RetailLossPreventionFeature</c> class defines the loss prevention feature and its prerequisites.
/// </summary>
[ExportAttribute(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
internal final class RetailLossPreventionFeature implements IFeatureMetadata, IFeaturePrerequisites 
{
    private static RetailLossPreventionFeature instance;

}

Method.Source:
private static void typeNew()
{
    instance = new RetailLossPreventionFeature();
}

[Hookable(false)]
public static RetailLossPreventionFeature instance()
{
    return RetailLossPreventionFeature::instance;
}

[Hookable(false)]
public boolean canEnable()
{
    // Ensure that Azure Data Lake has been configured prior to enabling loss prevention.
    BIMeasurementParameters entityStore = BIMeasurementParameters::find();
    return entityStore.CDSAEnabled;
}
