/// <summary>
/// The <c>RetailLoyaltyPointsCalculationCheckToggle</c> class represents the kill switch to stop skipping LoyaltyPointsCalculation for ordered qty check.
/// </summary>
internal final class RetailLoyaltyPointsCalculationCheckToggle extends WHSEnabledByDefaultToggle
{
    private static RetailLoyaltyPointsCalculationCheckToggle singleton;

}

Method.Source:
internal static RetailLoyaltyPointsCalculationCheckToggle instance()
{
    if (!singleton)
    {
        singleton = new RetailLoyaltyPointsCalculationCheckToggle();
    }

    return singleton;
}
