SourceCode.Declaration:
using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;
using Microsoft.Dynamics.BusinessPlatform.SharedTypes;

/// <summary>
/// The <c>RetailOmniChannelPaymentsFeature </c> class defines the omni-channel payments feature.
/// </summary>
[ExportAttribute(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
internal final class RetailOmniChannelPaymentsFeature implements IFeatureMetadata, IFeatureMetadataEnablementNotifiable
{
    private static RetailOmniChannelPaymentsFeature instance;

}

Method.Source:
private static void typeNew()
{
    instance = new RetailOmniChannelPaymentsFeature();
}

[Hookable(false)]
public boolean isEnabledByDefault()
{
    // For backwards compatibility, if someone has already enabled this configuration flag in Commerce shared parameters,
    // we will enable this feature. Otherwise, it will default to false.
    RetailSharedParameters parameters = RetailSharedParameters::find();
    return parameters.EnableOmniChannelPayments;
}

[Hookable(false)]
public void onEnabled()
{
    ttsbegin;

    RetailSharedParameters parameters = RetailSharedParameters::find(true);
    parameters.EnableOmniChannelPayments = true;
    parameters.doUpdate();

    ttscommit;
}

[Hookable(false)]
public void onDisabled()
{
    ttsbegin;

    RetailSharedParameters parameters = RetailSharedParameters::find(true);
    parameters.EnableOmniChannelPayments = false;
    parameters.doUpdate();

    ttscommit;
}
