/// <summary>
/// Gets or sets the payment access code.
/// </summary>
/// <param name = "_accessCode">The updated payment access code.</param>
/// <returns>The current payment access code.</returns>
[Hookable(false)]
public str parmAccessCode(str _accessCode = accessCode)
{
    accessCode = _accessCode;
    return accessCode;
}

