    finally
    {
        if (!success)
        {
            str errMsg = RetailTransactionServiceUtilities::getInfologMessages(fromLine);
            AppLogging::EventWriteRealTimeServiceContractMethodFailedError(funcName(), errMsg);
        }
    }
