using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;
using Microsoft.Dynamics.BusinessPlatform.SharedTypes;
using Microsoft.Dynamics.Commerce.Headquarters.Instrumentation;

/// <summary>
/// The <c>RetailRestrictCreditCardUsageFeature</c> class defines the enable retail restrict credit card usage feature.
/// </summary>
[FeatureMetadataConfigurationKey(configurationKeyStr(Retail))]
[ExportAttribute(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[InternalUseOnly]
internal final class RetailRestrictCreditCardUsageFeature implements IFeatureMetadata, IFeatureMetadataEnablementNotifiable
{
    private static RetailRestrictCreditCardUsageFeature instance;

}

Method.Source:
private static void TypeNew()
{
    instance = new RetailRestrictCreditCardUsageFeature();
}

[Hookable(false)]
public void onEnabled()
{
    MCROrderParameters  mcrOrderParameters = MCROrderParameters::find(true);
    mcrOrderParameters.MCRAllowCustomerCardOnFile = NoYes::Yes;
    mcrOrderParameters.update();

    CommerceHQEventSource eventSource = CommerceHQEventSource::Log;
    eventSource.EventWritePaymentsRetailRestrictCreditCardUsageFeatureEnabled();
}

[Hookable(false)]
public void onDisabled()
{
    CommerceHQEventSource eventSource = CommerceHQEventSource::Log;
    eventSource.EventWritePaymentsRetailRestrictCreditCardUsageFeatureDisabled();
}
