using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// The <c>RetailSalesAgreementPriceSupportFeature</c> class enables customer to apply sales agreement to sales orders in commerce channels.
/// </summary>
[Export(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
internal final class RetailSalesAgreementPriceSupportFeature implements IFeatureLifecycle, IFeatureMetadata
{
    private static RetailSalesAgreementPriceSupportFeature instance;

}

Method.Source:
private static void TypeNew()
{
    instance = new RetailSalesAgreementPriceSupportFeature();
}

[Hookable(false)]
public static RetailSalesAgreementPriceSupportFeature instance()
{
    return instance;
}

[Hookable(false)]
public boolean isEnabledByDefault()
{
    // Enabled by default in 10.0.32.
    return true;
}
