/// <summary>
/// Gets file name for sliced price report.
/// </summary>
/// <param name = "_storeNumber">Store number.</param>
/// <returns>File name for sliced price report</returns>
internal static str getFileName(RetailStoreId _storeNumber)
{
    return strFmt("@RetailPricing:SlicedPriceReportFileName", _storeNumber);
}
