Method.Source:
/// <summary>
/// Builds a string that contains the device information for printing a report based on the current
/// print medium type, file format, and image format.
/// </summary>
/// <returns>
/// String that contains the device information.
/// </returns>
public str deviceInfo()
{
    if (deviceInfo)
    {
        return deviceInfo;
    }

    return super();
}

/// <summary>
/// Sets the device information.
/// </summary>
/// <param name = "_deviceInfo">The device information.</param>
public void setDeviceInfo(str _deviceInfo)
{
    deviceInfo = _deviceInfo;
}
