SourceCode.Declaration:
using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// The <c>SalesD365SalesPriceMasterFeature</c> class defines the feature management feature,
/// when enabled Supply Chain Management is the price master and disable all price calculations in Dynamics 365 Sales application.
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public final class SalesD365SalesPriceMasterFeature implements IFeatureMetadata, IFeatureLifecycle, IFeatureMetadataEnablementNotifiable
{
    private static SalesD365SalesPriceMasterFeature singleton;

}

Method.Source:
[Hookable(false)]
public static SalesD365SalesPriceMasterFeature instance()
{
    if (!singleton)
    {
        singleton = new SalesD365SalesPriceMasterFeature();
    }

    return singleton;
}

internal boolean isFeatureAndConfigurationkeyEnabled()
{
    return this.isEnabled() && this.isConfigurationKeyEnabled();
}

[Hookable(false)]
public void onEnabled()
{
    // Parameter should not be set when feature is enabled.
}
