using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.Implementation;

/// <summary>
/// The <c>SalesD365SalesUpdatePricesAndTotalsFeature</c> class defines the feature management feature,
/// when enabled Supply Chain Management is having ability to push prices and totals to Dynamics 365 Sales manually or in a batch.
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public final class SalesD365SalesUpdatePricesAndTotalsFeature implements IFeatureMetadata, IFeatureLifecycle, IFeatureDependencies, IFeatureMetadataEnablementNotifiable
{
    private static SalesD365SalesUpdatePricesAndTotalsFeature singleton;

}

Method.Source:
[Hookable(false)]
public static SalesD365SalesUpdatePricesAndTotalsFeature instance()
{
    if (!singleton)
    {
        singleton = new SalesD365SalesUpdatePricesAndTotalsFeature();
    }

    return singleton;
}

internal boolean isFeatureAndConfigurationkeyEnabled()
{
    return this.isEnabled() && this.isConfigurationKeyEnabled();
}

[Hookable(false)]
public IFeature[] dependsOnAllFeatures()
{
    var result = new IFeature[1]();
    result.SetValue(SalesQuotationD365SalesFeature::instance(), 0);
    return result;
}

[Hookable(false)]
public void onEnabled()
{
    // Parameter should not be set when feature is enabled.
}
