SourceCode.Declaration:
using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// The <c>SalesPurchSyncDeliveryInfoFeature</c> class defines the feature management feature,
/// when enabled provides new parameters to control delivery information synchronization (create and update) between sales order and purchase order.
/// This feature controls the logic for line and header level for all scenarios (stock, direct delivery and intercompany).
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public final class SalesPurchSyncDeliveryInfoFeature implements IFeatureMetadata, IFeatureLifecycle, IFeatureMetadataEnablementNotifiable
{
    private static SalesPurchSyncDeliveryInfoFeature singleton;

}

Method.Source:
[Hookable(false)]
public static SalesPurchSyncDeliveryInfoFeature instance()
{
    if (!singleton)
    {
        singleton = new SalesPurchSyncDeliveryInfoFeature();
    }

    return singleton;
}

[Hookable(false)]
public void onEnabled()
{
    ttsbegin;

    PurchParameters purchParameters;
    while select forupdate crosscompany purchParameters
    {
        changeCompany(purchParameters.DataAreaId)
        {
            purchParameters.DeliveryInfoSyncOnCreateStock =  NoYes::Yes;
            purchParameters.DeliveryInfoSyncOnCreateDirectDelivery = NoYes::Yes;
            purchParameters.DeliveryInfoSyncOnUpdateDirectDelivery = NoYes::Yes;
            purchParameters.doUpdate();
        }
    }

    ttscommit;
}
