using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// The <c>SalesQuotationD365AutoSyncLineDataAndTotalsFeature</c> class defines the feature management feature,
/// the feature enables the automatic synchronization of sales quotation and sales order line data (including line price and discount) and line subtotals and header totals
/// from Supply Chain Management to Dynamics 365 Sales when the line is created or updated from Dynamics 365 Sales. 
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
internal final class SalesQuotationD365AutoSyncLineDataAndTotalsFeature implements IFeatureMetadata, IFeatureLifecycle, IFeatureMetadataEnablementNotifiable
{
    private static SalesQuotationD365AutoSyncLineDataAndTotalsFeature singleton;

}

Method.Source:
[Hookable(false)]
public static SalesQuotationD365AutoSyncLineDataAndTotalsFeature instance()
{
    if (!singleton)
    {
        singleton = new SalesQuotationD365AutoSyncLineDataAndTotalsFeature();
    }

    return singleton;
}

internal boolean isFeatureAndConfigurationkeyEnabled()
{
    return this.isEnabled() && this.isConfigurationKeyEnabled();
}
