using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.Implementation;

/// <summary>
/// The <c>SalesQuotationD365DefaultSalesOrderFeature</c> class defines the feature management feature.
/// When enabled, sales quotation data from Supply Chain Management is copied to sales order synced from Dynamics 365 Sales.
/// </summary>
[ExportAttribute(identifierstr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
public final class SalesQuotationD365DefaultSalesOrderFeature implements IFeatureMetadata, IFeatureLifecycle, IFeatureDependencies, IFeatureMetadataEnablementNotifiable
{
    private static SalesQuotationD365DefaultSalesOrderFeature singleton;

}

Method.Source:
[Hookable(false)]
public static SalesQuotationD365DefaultSalesOrderFeature instance()
{
    if (!singleton)
    {
        singleton = new SalesQuotationD365DefaultSalesOrderFeature();
    }

    return singleton;
}

internal boolean isFeatureAndConfigurationkeyEnabled()
{
    return this.isEnabled() && this.isConfigurationKeyEnabled();
}

[Hookable(false)]
public IFeature[] dependsOnAllFeatures()
{
    var result = new IFeature[1]();
    result.SetValue(SalesQuotationD365SalesFeature::instance(), 0);
    return result;
}

[Hookable(false)]
public void onEnabled()
{
    // Parameter should not be set when feature is enabled.
}
