/// <summary>
/// This contract class represents Args object from Copilot request JSON.
/// </summary>
[DataContractAttribute]
internal final class SrsCopilotArgsContract
{
    str dataTableName;
    str dataTableFieldName;
    str dataTableFieldValue;
    int lookupTable;
    int lookupField;
    str lookupValue;
    str menuItemName;
    MenuItemType menuItemType;
    str name;
    str parm;
    int parmEnumType;
    str parmEnum;
    str callerName;
    str printType;

}

