/// <summary>
/// Constants for enabling the small parcel shipping processes
/// </summary>
internal class TMSProcessXmlConstants
{
    /// <summary>
    /// Key to store the carrier customer account number for accessing the carrier external service.
    /// </summary>
    internal static const str CarrierCustomerAccountNumber = 'CarrierCustomerAccountNumber';

    /// <summary>
    /// Key to store the accessorial delivery type.
    /// </summary>
    internal static const str AccessorialDeliveryType = 'AccessorialDeliveryType';

    /// <summary>
    /// Key to store the shipping label request type.
    /// </summary>
    internal static const str ShippingLabelRequestType = 'ShippingLabelRequestType';

    /// <summary>
    /// Key to store the container shipping label type.
    /// </summary>
    internal static const str ContainerShippingLabelType = 'ContainerShippingLabelType';

    /// <summary>
    /// Key to store the container shipping label.
    /// </summary>
    internal static const str ContainerShippingLabel = 'ContainerShippingLabel';

    /// <summary>
    /// Key to store the container return shipping label type.
    /// </summary>
    internal static const str ContainerReturnShippingLabelType = 'ContainerReturnShippingLabelType';

    /// <summary>
    /// Key to store the container return shipping label.
    /// </summary>
    internal static const str ContainerReturnShippingLabel = 'ContainerReturnShippingLabel';

}
