using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
///     The <c>TSDisplayResourceGroupProjFeature</c> class controls the feature to
///     display projects assigned to resource via resource group in the Timesheet mobile app.
/// </summary>
[ExportAttribute(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
internal final class TSDisplayResourceGroupProjFeature implements IFeatureMetadata
{
    private static TSDisplayResourceGroupProjFeature instance = new TSDisplayResourceGroupProjFeature();
    private const URL LearnMoreUrl = "https://aka.ms/DisplayAssignedProjectsViaResourceGroup";

}

Method.Source:
/// <summary>
/// Gets the label Id of the feature that is shown on the feature management workspace
/// </summary>
/// <returns>Label for the feature.</returns>
[Hookable(false)]
public FeatureLabelId label()
{
    return literalStr("@Timesheet:DisplayAssignedByResourceGroupProjects");
}

/// <summary>
/// Gets the application module of the feature that is displayed on the feature management workspace.
/// </summary>
/// <returns>
/// The application module value of the feature that is displayed on the feature management workspace.
/// </returns>
[Hookable(false)]
public int module()
{
    return FeatureModuleV0::ProjectManagementAndAccounting;
}

/// <summary>
/// Gets the label Id that is displayed as summary of the feature.
/// </summary>
/// <returns>Summary for the feature.</returns>
[Hookable(false)]
public FeatureLabelId summary()
{
    return literalStr("@Timesheet:DisplayAssignedByResourceGroupProjectDetails");
}

/// <summary>
/// A forward link to the Microsoft documentation of the feature.
/// </summary>
/// <returns> URL to the Microsoft documentation of the feature.</returns>
[Hookable(false)]
public WebSiteURL learnMoreUrl()
{
    return LearnMoreUrl;
}

/// <summary>
/// Determines whether the feature is enabled by default.
/// </summary>
/// <returns>true when feature is enabled by default; otherwise false.</returns>
[Hookable(false)]
public boolean isEnabledByDefault()
{
    return true;
}

/// <summary>
///  Determines whether the user can disable the feature.
/// </summary>
/// <returns>true, always as user should've have capability to disable feature.</returns>
[Hookable(false)]
public boolean canDisable()
{
    return true;
}
