/// <summary>
/// Stores commonly used constants for Tax 1099.
/// </summary>
internal static class Tax1099Constants
{
    public static const int FirstDay = 1;
    public static const int FirstMonth = 1;
    public static const int LastDay = 31;
    public static const int LastMonth = 12;

    // Validation of the Tax1099BoxDetail.Tax1099TaxYear field allows 
    // the field to be set to a specific amount of years in the past or future
    public static const int Tax1099TaxYear_MaxNumOfYearsInFuture = 10;
    public static const int Tax1099TaxYear_MaxNumOfYearsInPast = 15;
}
