SourceCode.Declaration:
/// <summary>
/// The <c>Tax1099TransmitterValidateParameters</c> class contains the parameters that can be validated when transmitting information to the IRS.
/// </summary>
public final class Tax1099TransmitterValidateParameters
{
    public boolean validateName;
    public boolean validateStreet;
    public boolean validateCity;
    public boolean validateState;
    public boolean validatePostalCode;
    public boolean validateContactName;
    public boolean validateContactPhone;
    public boolean validateTax1099RegNum;
    public boolean validateTcc;
}

Method.Source:
internal static Tax1099TransmitterValidateParameters construct()
{
    Tax1099TransmitterValidateParameters parameters = new Tax1099TransmitterValidateParameters();
    parameters.validateName = true;
    parameters.validateState = true;
    parameters.validateCity = true;
    parameters.validateState = true;
    parameters.validatePostalCode = true;
    parameters.validateContactName = true;
    parameters.validateContactPhone = true;
    parameters.validateTax1099RegNum = true;
    parameters.validateTcc = true;

    return parameters;
}
