/// <summary>
/// Copies to an address object from <c>TaxIntegrationAddressObject</c>.
/// </summary>
/// <param name = "_origin">The origin <c>TaxIntegrationAddressObject</c>.</param>
public void copyFromAddress(
    TaxIntegrationAddressObject _origin)
{
    this.setZipCode(_origin.getZipCode());
    this.setCity(_origin.getCity());
    this.setState(_origin.getState());
    this.setCountry(_origin.getCountry());
    this.setCountryRegionType(_origin.getCountryRegionType());
    if (TaxIntegrationUtils::isTaxIntegrationEnabledForTaxSolutionProvider())
    {
        this.setStreet(_origin.getStreet());
        this.setLatitude(_origin.getLatitude());
        this.setLongitude(_origin.getLongitude());
    }
}
