 /// <summary>
/// Constructs a <c>TaxIntegrationDocumentObject</c>.
/// </summary>
/// <param name = "_tableId">The table id.</param>
/// <param name = "_recId">The rec id.</param>
protected void new(RefTableId _tableId, RefRecId _recId)
{
    super(_tableId, _recId);

    this.lineMap = new Map(Types::String, Types::AnyType);
    this.chargeMap = new Map(Types::String, Types::AnyType);
    this.lineChargeMap = new Map(Types::String, Types::AnyType);
    this.shipmentOriginAddress = TaxIntegrationAddressObject::construct();
    this.shipmentDestinationAddress = TaxIntegrationAddressObject::construct();
    this.invoiceOriginAddress = TaxIntegrationAddressObject::construct();
    this.invoiceDestinationAddress = TaxIntegrationAddressObject::construct();
    this.inventory = TaxIntegrationInventoryObject::construct();
    this.company = curExt();

    this.errorCollection = TaxIntegrationErrorCollection::construct();
    this.quantityType =  TaxIntegrationQuantityType::construct();
    this.taxJurisdictionParameters = TaxIntegrationTaxJurisdictionParameters::construct();
    this.contextMap = new Map(Types::String, Types::AnyType);
    this.setTaxable(true);
}

/// <summary>
/// Constructs a <c>TaxIntegrationDocumentObject</c>.
/// </summary>
/// <param name = "_tableId">The table id.</param>
/// <param name = "_recId">The rec id.</param>
/// <returns>The new <c>TaxIntegrationDocumentObject</c>.</returns>
public final static TaxIntegrationDocumentObject construct(RefTableId _tableId, RefRecId _recId)
{
    return new TaxIntegrationDocumentObject(_tableId, _recId);
}

/// <summary>
/// Constructs a <c>TaxIntegrationDocumentObject</c>.
/// </summary>
/// <param name = "_record">The record.</param>
/// <returns>The new <c>TaxIntegrationDocumentObject</c>.</returns>
public final static TaxIntegrationDocumentObject constructWithRecord(Common _record)
{
    TaxIntegrationDocumentObject document = new TaxIntegrationDocumentObject(_record.TableId, _record.RecId);
    document.setLocalRecord(_record);
    return document;
}

/// <summary>
/// Gets context. The context object is saved as per company, in case of intercompany scenario.
/// </summary>
/// <returns>The <c>TaxIntegrationDocumentObjectContext</c>.</returns>
internal TaxIntegrationDocumentObjectContext getContext()
{
    TaxIntegrationDocumentObjectContext result;
    str dataArea = curext();

    if (contextMap.exists(dataArea))
    {
        result = contextMap.lookup(dataArea);
    }
    else
    {
        result = new TaxIntegrationDocumentObjectContext();
        contextMap.add(dataArea, result);
    }

    return result;
}

