/// <summary>
/// Clears document tax adjustment.
/// </summary>
public void clearTaxAdjustment()
{
    SetEnumerator headerChargeSet = this.getChargeSet().getEnumerator();
    while (headerChargeSet.moveNext())
    {
        TaxIntegrationLineObject headerCharge = headerChargeSet.current();
        headerCharge.clearTaxAdjustment();
    }

    SetEnumerator lineSet = this.getLineSet().getEnumerator();
    while (lineSet.moveNext())
    {
        TaxIntegrationLineObject line = lineSet.current();
        line.clearTaxAdjustment();
    }
}

/// <summary>
/// Counts the number of all tax lines in this document.
/// </summary>
/// <returns>The number of tax lines.</returns>
public int countAllTaxLines()
{
    int taxLineCount = 0;
    var chargeEnumerator = this.getChargeSet().getEnumerator();
    while (chargeEnumerator.moveNext())
    {
        TaxIntegrationLineObject charge = chargeEnumerator.current();
        taxLineCount += charge.getTaxLineCount();
    }
    var lineEnumerator = this.getLineSet().getEnumerator();
    while (lineEnumerator.moveNext())
    {
        TaxIntegrationLineObject line = lineEnumerator.current();
        taxLineCount += line.getTaxLineCount();
        chargeEnumerator = line.getChargeSet().getEnumerator();
        while (chargeEnumerator.moveNext())
        {
            TaxIntegrationLineObject charge = chargeEnumerator.current();
            taxLineCount += charge.getTaxLineCount();
        }
    }
    return taxLineCount;
}

/// <summary>
/// Counts the number of all charge lines in this document, including header and line charges.
/// </summary>
/// <returns>The number of charge lines.</returns>
public int countAllCharges()
{
    int chargeCount = this.getChargeCount();
    var lineEnumerator = this.getLineSet().getEnumerator();
    while (lineEnumerator.moveNext())
    {
        TaxIntegrationLineObject line = lineEnumerator.current();
        chargeCount += line.getChargeCount();
    }
    return chargeCount;
}

