/// <summary>
/// Determines the Inventory, Customer and Vendor party type by tax integration business process.
/// </summary>
/// <returns>The Inventory, Customer and Vendor party type.</returns>
public TaxIntegrationPartyType getPartyType()
{
    TaxIntegrationPartyType partyType;
    boolean isJournal;

    switch (businessProcess)
    {
        case TaxIntegrationBusinessProcess::Sales:
        case TaxIntegrationBusinessProcess::FTI:
            partyType = TaxIntegrationPartyType::Cust;
            break;
        case TaxIntegrationBusinessProcess::Purchase:
        case TaxIntegrationBusinessProcess::SADDocument:
            partyType = TaxIntegrationPartyType::Vend;
            break;
        case TaxIntegrationBusinessProcess::Inventory:
            partyType = TaxIntegrationPartyType::Invent;
            break;
        case TaxIntegrationBusinessProcess::Journal:
            isJournal = true;
            break;
        case TaxIntegrationBusinessProcess::Project:
            if (this.isJournal() || this.getHeadingTableId() == tableNum(ProjAdvancedJournalLine))
            {
                isJournal = true;
            }
            else
            {
                partyType = TaxIntegrationPartyType::Cust;
            }
            break;
    }

    if (isJournal)
    {
        if (this.getAccountType() == LedgerJournalACType::Cust)
        {
            partyType = TaxIntegrationPartyType::Cust;
        }
        else if (this.getAccountType() == LedgerJournalACType::Vend)
        {
            partyType = TaxIntegrationPartyType::Vend;
        }
    }

    return partyType;
}

/// <summary>
/// Determines whether the current document object is a tax integration journal document object.
/// </summary>
/// <returns>true, if it is a journal document object; otherwise, false.</returns>
public boolean isJournal()
{
    // Only work for journal transaction.
    return false;
}

